SELECT Wareroom_Id AS Inbound_Wareroom_Id, Wareroom_Code AS Inbound_Wareroom_Code, Wareroom_Name AS Inbound_Wareroom_Name, Wareroom_Barcode AS Inbound_Wareroom_Barcode, Wareroom_Description AS Inbound_Wareroom_Description FROM Wareroom WHERE Wareroom_Id = :id AND Tenant_Id = :tenant AND NOT Is_Deleted AND Is_Active