SELECT Wareroom_Id AS Outbound_Wareroom_Id, Wareroom_Code AS Outbound_Wareroom_Code, Wareroom_Name AS Outbound_Wareroom_Name, Wareroom_Barcode AS Outbound_Wareroom_Barcode, Wareroom_Description AS Outbound_Wareroom_Description FROM Wareroom WHERE Wareroom_Id = :id AND Tenant_Id = :tenant AND NOT Is_Deleted AND Is_Active