SELECT Wareroom_Id Outbound_Wareroom_Id, Wareroom_Code Outbound_Wareroom_Code, Wareroom_Name Outbound_Wareroom_Name, Wareroom_Barcode Outbound_Wareroom_Barcode, Wareroom_Description Outbound_Wareroom_Description, Is_Active FROM Wareroom WHERE Tenant_Id = :tenant AND NOT Is_Deleted AND Is_Active{filter} ORDER BY {order_By}Wareroom_Code OFFSET :begin_No LIMIT :page_Size