INSERT INTO Outbound_Bill_Detail (Bill_Id, Sn, Related_Bill_Type, Related_Bill_Detail_Id, Department_Id, Owner_Id, Material_Id,
		Outbound_Quantity, Real_Outbound_Quantity, Outbound_Unit, Outbound_Packing_Size, Outbound_Piece, Real_Outbound_Piece, Retail_Amount, Comment)
	VALUES (CURRVAL(CAST('outbound_bill_bill_id_seq' AS regclass)), :Sn, :Bill_Type, :Bill_Detail_Id, :Department_Id, :Owner_Id, :Material_Id,
		CAST(:Real_Outbound_Quantity AS DECIMAL) + CAST(:Discount_Outbound_Quantity AS DECIMAL), :Real_Outbound_Quantity, :Outbound_Unit, :Outbound_Packing_Size, CAST(:Real_Outbound_Piece AS INT8) + CAST(:Discount_Outbound_Piece AS INT8), :Real_Outbound_Piece, CAST(:Retail_Price AS DECIMAL) * CAST(:Real_Outbound_Quantity AS DECIMAL) * CAST(:Outbound_Packing_Size AS INT8) / CAST(:Retail_Packing_Size AS INT8), :Comment)